mapper.select:
select nc_name from node_cmd nc
where
nc.node_id = #{nodeId,jdbcType=BIGINT}
and nc.nc_name in

    #{ncName}

select nc_content
from node_cmd nc
where
nc.node_id = #{nodeId,jdbcType=BIGINT}
and nc.nc_content in

    #{ncContent}

select nc_id
from node_cmd nc
where nc.node_id = #{nodeId,jdbcType=BIGINT}
